import React, { useEffect, useState } from "react";
import Splash from "./Splash";
import moment from 'moment-hijri';

function App() {
  const [count, setCount] = useState(0);
  const [bgColor, setBgColor] = useState("#0d6efd");
  const [showSplash, setShowSplash] = useState(true);
  const [hijriDate, setHijriDate] = useState("");
  const [installPrompt, setInstallPrompt] = useState(null);

  // Proper PWA installation handling
  useEffect(() => {
    const handler = (e) => {
      e.preventDefault();
      setInstallPrompt(e);
    };
    window.addEventListener('beforeinstallprompt', handler);
    return () => window.removeEventListener('beforeinstallprompt', handler);
  }, []);

  const handleInstall = async () => {
    if (!installPrompt) return;
    const result = await installPrompt.prompt();
    console.log(`Install ${result.outcome}`);
    setInstallPrompt(null);
  };

  // Hijri date calculation
  useEffect(() => {
    const splashTimeout = setTimeout(() => setShowSplash(false), 3000);

    try {
      const hijriMoment = moment();
      setHijriDate(`${hijriMoment.format('iD')} ${hijriMoment.format('iMMMM')} ${hijriMoment.format('iYYYY')} AH`);
    } catch (error) {
      console.error("Hijri date error:", error);
      setHijriDate(new Date().toLocaleDateString('en-US', {
        day: 'numeric',
        month: 'long',
        year: 'numeric',
        calendar: 'islamic'
      }));
    }

    return () => clearTimeout(splashTimeout);
  }, []);

  if (showSplash) {
    return <Splash onFinish={() => setShowSplash(false)} />;
  }

  return (
    <div className="app">
      <div className="hijri-banner">Hijri Date: {hijriDate}</div>
      <div className="screen" style={{ backgroundColor: bgColor }}>
        {installPrompt && (
          <button 
            onClick={handleInstall}
            style={{
              position: 'absolute',
              top: '10px',
              right: '10px',
              padding: '8px 16px',
              background: '#fff',
              color: bgColor,
              border: 'none',
              borderRadius: '20px',
              fontWeight: 'bold',
              zIndex: 1000
            }}
          >
            Install App
          </button>
        )}

        <div className="counter-body" style={{ borderColor: bgColor }}>
          <div className="display">{String(count).padStart(4, "0")}</div>

          <div className="buttons">
            <button
              className="icon-btn"
              onClick={() => setCount(0)}
              title="Reset"
            >
              ðŸ”„
            </button>
          </div>

          <button
            className="main-btn"
            onClick={() => setCount(count + 1)}
            style={{
              "--btn-bg": "#fff",
              "--btn-hover-bg": bgColor
            }}
          ></button>
        </div>

        <footer>
          <input
            type="color"
            value={bgColor}
            onChange={(e) => setBgColor(e.target.value)}
          />
          <h2><b>Theme</b></h2>
        </footer>
      </div>
    </div>
  );
}

export default App;
