    "Sha'ban",
    "Ramadan",
    "Shawwal",
    "Dhul-Qa'dah",
    "Dhul-Hijjah",
  ];

  const monthName = hijriMonths[hijri.month - 1];
  const formatted = `Hijri Date: ${hijri.day} ${monthName} ${hijri.year} AH`;

  setHijriDate(formatted);

