import React, { useEffect, useState } from "react";
import Splash from "./Splash";
import moment from 'moment-hijri';

function App() {
  const [count, setCount] = useState(0);
  const [bgColor, setBgColor] = useState("#0d6efd");
  const [showSplash, setShowSplash] = useState(true);
  const [hijriDate, setHijriDate] = useState("");
  const [installPrompt, setInstallPrompt] = useState(null);
  const [showInstallBtn, setShowInstallBtn] = useState(false);

  // 1. PROPER INSTALL PROMPT HANDLING
  useEffect(() => {
    const handleBeforeInstallPrompt = (e) => {
      e.preventDefault();
      setInstallPrompt(e);
      setShowInstallBtn(true);

      // For debugging:
      console.log('PWA installable!', e);
    };

    window.addEventListener('beforeinstallprompt', handleBeforeInstallPrompt);

    return () => {
      window.removeEventListener('beforeinstallprompt', handleBeforeInstallPrompt);
    };
  }, []);

  // 2. INSTALL BUTTON CLICK HANDLER
  const handleInstallClick = async () => {
    if (!installPrompt) return;

    try {
      // Trigger native install prompt
      const result = await installPrompt.prompt();

      // Wait for user choice
      const { outcome } = await result;
      console.log('User response:', outcome);

      // Reset the prompt
      setInstallPrompt(null);
      setShowInstallBtn(false);
    } catch (err) {
      console.error('Install failed:', err);
    }
  };

  // 3. HIJRI DATE LOGIC (unchanged)
  useEffect(() => {
    const splashTimeout = setTimeout(() => setShowSplash(false), 3000);

    try {
      const hijriMoment = moment();
      setHijriDate(`${hijriMoment.format('iD')} ${hijriMoment.format('iMMMM')} ${hijriMoment.format('iYYYY')} AH`);
    } catch (error) {
      console.error("Error generating Hijri date:", error);
      setHijriDate("Hijri Date: Unavailable");
    }

    return () => clearTimeout(splashTimeout);
  }, []);

  if (showSplash) {
    return <Splash onFinish={() => setShowSplash(false)} />;
  }

  return (
    <div className="app">
      <div className="hijri-banner">Hijri Date: {hijriDate}</div>

      {/* 4. INSTALL BUTTON */}
      {showInstallBtn && (
        <button 
          onClick={handleInstallClick}
          style={{
            position: 'fixed',
            bottom: '20px',
            right: '20px',
            padding: '10px 15px',
            background: '#ffffff',
            color: bgColor,
            border: 'none',
            borderRadius: '5px',
            fontWeight: 'bold',
            boxShadow: '0 2px 5px rgba(0,0,0,0.2)',
            zIndex: 1000
          }}
        >
          Install App
        </button>
      )}

      {/* Rest of your existing UI */}
      <div className="screen" style={{ backgroundColor: bgColor }}>
        {/* ... your counter and other components ... */}
      </div>
    </div>
  );
}

export default App;
