import React, { useEffect, useState } from "react";
import Splash from "./Splash";
import { toHijri } from 'hijri-date';


function App() {
  const [count, setCount] = useState(0);
  const [bgColor, setBgColor] = useState("#0d6efd");
  const [showSplash, setShowSplash] = useState(true);
  const [hijriDate, setHijriDate] = useState("");

useEffect(() => {
  const today = new Date();

  try {
    const hijri = toHijri(today);  // Returns { day, month, year }

    const hijriMonths = [
      "Muharram", "Safar", "Rabi'ul-awwal", "Rabi'ul-thani",
      "Jumadal-awwal", "Jumadath-thani", "Rajab", "Sha'ban",
      "Ramadan", "Shawwal", "Dhul-Qa'dah", "Dhul-Hijjah"
    ];

    const monthName = hijriMonths[hijri.month - 1];  // Months are 1-12
    const formatted = `Hijri Date: ${hijri.day} ${monthName} ${hijri.year} AH`;
    setHijriDate(formatted);
  } catch (error) {
    console.error(error);
    setHijriDate("Error generating Hijri date.");
  }
}, []);
  // Show splash if still active
  if (showSplash) {
    return <Splash onFinish={() => setShowSplash(false)} />;
  }

  return (
    <div className="app">
<div className="hijri-banner">Hijri Date: {hijriDate}</div>
      <div className="screen" style={{ backgroundColor: bgColor }}>
        <div className="counter-body" style={{ borderColor: bgColor }}>
          <div className="display">{String(count).padStart(4, "0")}</div>

          <div className="buttons">
            <button
              className="icon-btn"
              onClick={() => setCount(0)}
              title="Reset"
            >
              ðŸ”„
            </button>
          </div>

          <button
            className="main-btn"
            onClick={() => setCount(count + 1)}
    style={{
    "--btn-bg": "#fff",
    "--btn-hover-bg": bgColor // or any dynamic value
  }}
          ></button>
        </div>

        <footer>
          <input
            type="color"
            value={bgColor}
            onChange={(e) => setBgColor(e.target.value)}
          />
          <h2>
            <b>Theme</b>
          </h2>
        </footer>
      </div>
    </div>
  );
}

export default App;
